import React, { useState } from 'react';

import { Box, Flex } from '../../atoms/layout';
import { Paragraph } from '../../atoms/typography';

export const FileItem: React.FC<FileItemProps> = ({ filename }) => {
	const [isOpen, setIsOpen] = useState(false);

	return (
		<Flex
			onClick={() => setIsOpen(!isOpen)}
			style={{ cursor: 'pointer' }}
			width="full"
			justifyContent="space-between"
			borderBottomWidth={2}
			borderStyle="fileItem"
			borderColor="grays.300"
		>
			<Box>
				<Paragraph>{filename}</Paragraph>
			</Box>
			{isOpen ? <img src="/assets/expand-less-black.svg" />
			: <img src="/assets/expand-more-black.svg" />}
		</Flex>
	);
};

interface FileItemProps {
	filename: string;
}
