import React, { useEffect, useState } from 'react';

import { Flex, Box } from '../../atoms/layout';
import { Button } from '../../atoms/input';

const timeScale = 50;

const size = 500;

export const Clock: React.FC = () => {
	const [degSec, setDegSec] = useState(0);
	const [degMin, setDegMin] = useState(0);
	const [degHou, setDegHou] = useState(0);

	useEffect(() => {
		const interval = setInterval(() => setDegSec(degSec + 6), 1000 / timeScale);
		return () => clearInterval(interval);
	}, [degSec]);

	useEffect(() => {
		const interval = setInterval(() => setDegMin(degMin + 6), 1000 * 60 / timeScale);
		return () => clearInterval(interval);
	}, [degMin]);

	useEffect(() => {
		const interval = setInterval(() => setDegHou(degHou + 15), 1000 * 60 * 60 / timeScale);
		return () => clearInterval(interval);
	}, [degHou]);

	return (
		<>
			<Flex
				height={size}
				width={size}
				bg="reds.100"
				borderRadius="100%"
				borderStyle="solid"
				borderWidth={2}
				justifyContent="center"
				alignItems="center"
			>
				<Box height="15px" width="15px" bg="black" borderRadius="100%" zIndex={10} />
				{[...Array(60).keys()].map(key => (
					<Box
						key={key}
						position="absolute"
						bg="black"
						height="10px"
						width="3px"
						style={{
							transform: `rotate(${(key + 1) * 6}deg) translateY(-245px)`,
						}}
					/>
				))}
				<Box // Second hand
					height={size / 2 - 30}
					width="5px"
					bg="reds.300"
					position="absolute"
					style={{
						transform: `rotate(${degSec}deg) translateY(${(size / 2 - 30) / 2}px)`,
					}}
				/>
				<Box // Minute hand
					height={size / 2 - 30}
					width="5px"
					bg="black"
					position="absolute"
					style={{
						transform: `rotate(${degMin}deg) translateY(-110px)`,
					}}
				/>
				<Box // Hour hand
					height={size / 2 - 50}
					width="5px"
					bg="black"
					position="absolute"
					style={{
						transform: `rotate(${degHou}deg) translateY(-95px)`,
					}}
				/>
			</Flex>
			<Button onClick={() => {
				setDegSec(0);
				setDegMin(0);
				setDegHou(0);
			}}>Reset clock</Button>
		</>
	);
};
