import React, { useEffect, useState } from 'react';

import { Flex, Box } from '../../atoms/layout';

const size = 500;
const indicatorSize = 10;

const getHandSize = (shorten: number) => size / 2 - shorten;
const getIndicatorOffset = () => ((size - indicatorSize) / 2);

const getDegSec = () => (360 / 60 * (new Date()).getSeconds());
const getDegMin = () => (360 / 60 * (new Date()).getMinutes());
const getDegHou = () => (360 / 12 * ((new Date()).getHours() > 11 ? (new Date()).getHours() - 11 : (new Date()).getHours()));

export const Clock: React.FC = () => {
	const [degSec, setDegSec] = useState(getDegSec());
	const [degMin, setDegMin] = useState(getDegMin());
	const [degHou, setDegHou] = useState(getDegHou());

	useEffect(() => {
		const interval = setInterval(() => {
			setDegSec(getDegSec());
			setDegMin(getDegMin());
			setDegHou(getDegHou());
		}, 1000);
		return () => clearInterval(interval);
	}, [degSec, degMin, degHou]);

	return (
		<Flex
			m="100px auto 0 auto"
			height={size}
			width={size}
			bg="reds.100"
			borderRadius="100%"
			borderStyle="solid"
			borderWidth={2}
			justifyContent="center"
			alignItems="center"
		>
			<Box height="15px" width="15px" bg="black" borderRadius="100%" zIndex={10} />
			{[...Array(60).keys()].map(key => (
				<Box
					key={key}
					position="absolute"
					bg="black"
					height={indicatorSize}
					width="3px"
					style={{
						transform: `rotate(${360 / 60 * (key + 1)}deg) translateY(-${getIndicatorOffset()}px)`,
					}}
				/>
			))}
			<ClockHand
				color="reds.300"
				handSize={getHandSize(30)}
				deg={degSec}
			/>
			<ClockHand
				color="black"
				handSize={getHandSize(30)}
				deg={degMin}
			/>
			<ClockHand
				color="black"
				handSize={getHandSize(100)}
				deg={degHou}
			/>
		</Flex>
	);
};

const ClockHand: React.FC<ClockHandProps> = ({ color, handSize, deg }) => (
	<Box
		height={handSize}
		width="5px"
		bg={color}
		position="absolute"
		style={{
			transform: `rotate(${deg}deg) translateY(-${handSize / 2}px)`,
		}}
	/>
);

interface ClockHandProps {
	color: string;
	handSize: number;
	deg: number;
}
