import React, { useEffect, useState } from 'react';

import { Flex, Box } from '../../atoms/layout';

export const Clock: React.FC = () => {
	const [deg, setDeg] = useState(0);

	useEffect(() => {
		const interval = setInterval(() => {
			deg++;
		}, 1000);

		return () => clearInterval(interval);
	}, [])

	return (
		<Flex
			height="500px"
			width="500px"
			backgroundColor="reds.100"
			borderRadius="100%"
			borderStyle="solid"
			borderWidth={2}
			justifyContent="center"
			alignItems="center"
		>
			<Box height="15px" width="15px" backgroundColor="black" borderRadius="100%" />
			<Box
				height="220px"
				width="5px"
				backgroundColor="black"
				position="absolute"
				style={{
					transform: `rotate(${deg}deg) translateY(-110px)`,
				}}
			/>
		</Flex>
	);
};
