import React, { useEffect, useState } from 'react';

import { Flex, Box } from '../../atoms/layout';

const size = 500;

const getHandSize = (shorten: number) => size / 2 - shorten;

const getDegSec = () => (360 / 60 * (new Date()).getSeconds());
const getDegMin = () => (360 / 60 * (new Date()).getMinutes());
const getDegHou = () => (360 / 12 * ((new Date()).getHours() > 11 ? (new Date()).getHours() - 11 : (new Date()).getHours()));

export const Clock: React.FC = () => {
	console.log(15 * (new Date()).getHours() - 12, getDegHou());
	const [degSec, setDegSec] = useState(getDegSec());
	const [degMin, setDegMin] = useState(getDegMin());
	const [degHou, setDegHou] = useState(getDegHou());

	useEffect(() => {
		const interval = setInterval(() => {
			setDegSec(getDegSec());
			setDegMin(getDegMin());
			setDegHou(getDegHou());
		}, 1000);
		return () => clearInterval(interval);
	}, [degSec, degMin, degHou]);

	return (
		<Flex
			m="100px auto 0 auto"
			height={size}
			width={size}
			bg="reds.100"
			borderRadius="100%"
			borderStyle="solid"
			borderWidth={2}
			justifyContent="center"
			alignItems="center"
		>
			<Box height="15px" width="15px" bg="black" borderRadius="100%" zIndex={10} />
			{[...Array(60).keys()].map(key => (
				<Box
					key={key}
					position="absolute"
					bg="black"
					height="10px"
					width="3px"
					style={{
						transform: `rotate(${(key + 1) * 6}deg) translateY(-245px)`,
					}}
				/>
			))}
			<Box // Second hand
				height={getHandSize(30)}
				width="5px"
				bg="reds.300"
				position="absolute"
				style={{
					transform: `rotate(${degSec}deg) translateY(-${getHandSize(30) / 2}px)`,
				}}
			/>
			<Box // Minute hand
				height={getHandSize(30)}
				width="5px"
				bg="black"
				position="absolute"
				style={{
					transform: `rotate(${degMin}deg) translateY(-${getHandSize(30) / 2}px)`,
				}}
			/>
			<Box // Hour hand
				height={getHandSize(100)}
				width="5px"
				bg="black"
				position="absolute"
				style={{
					transform: `rotate(${degHou}deg) translateY(-${getHandSize(100) / 2}px)`,
				}}
			/>
		</Flex>
	);
};
