import React, { useEffect, useState } from 'react';

import { Flex, Box } from '../../atoms/layout';

export const Clock: React.FC = () => {
	const [degSec, setDegSec] = useState(0);
	const [degMin, setDegMin] = useState(0);
	const [degHou, setDegHou] = useState(0);

	useEffect(() => {
		const interval = setInterval(() => {
			setDegSec(degSec + 6);
		}, 1000);

		return () => clearInterval(interval);
	}, [degSec])

	return (
		<Flex
			height="500px"
			width="500px"
			backgroundColor="reds.100"
			borderRadius="100%"
			borderStyle="solid"
			borderWidth={2}
			justifyContent="center"
			alignItems="center"
		>
			<Box height="15px" width="15px" backgroundColor="black" borderRadius="100%" />
			<Box // Second hand
				height="220px"
				width="5px"
				backgroundColor="black"
				position="absolute"
				style={{
					transform: `rotate(${degSec}deg) translateY(-110px)`,
				}}
			/>
			<Box // Minute hand
				height="220px"
				width="5px"
				backgroundColor="black"
				position="absolute"
				style={{
					transform: `rotate(${degMin}deg) translateY(-110px)`,
				}}
			/>
			<Box // Hour hand
				height="220px"
				width="5px"
				backgroundColor="black"
				position="absolute"
				style={{
					transform: `rotate(${degHou}deg) translateY(-110px)`,
				}}
			/>
		</Flex>
	);
};
