import React, { useEffect, useState } from 'react';

import { Flex, Box } from '../../atoms/layout';
import { Button } from '../../atoms/input';

const size = 500;

const sec = 1000;
const min = sec * 60;
const hou = min * 60;

const getDegSec = () => (6 * (new Date()).getSeconds());
const getDegMin = () => (6 * (new Date()).getMinutes());
const getDegHou = () => (15 * (1 + (new Date()).getHours()));

export const Clock: React.FC = () => {
	const [degSec, setDegSec] = useState(getDegSec());
	const [degMin, setDegMin] = useState(getDegMin());
	const [degHou, setDegHou] = useState(getDegHou());

	useEffect(() => {
		const interval = setInterval(() => setDegSec(getDegSec()), sec);
		return () => clearInterval(interval);
	}, [degSec]);

	useEffect(() => {
		const interval = setInterval(() => setDegMin(getDegMin()), min);
		return () => clearInterval(interval);
	}, [degMin]);

	useEffect(() => {
		const interval = setInterval(() => setDegHou(getDegHou()), hou);
		return () => clearInterval(interval);
	}, [degHou]);

	return (
		<Flex
			m="100px auto 0 auto"
			height={size}
			width={size}
			bg="reds.100"
			borderRadius="100%"
			borderStyle="solid"
			borderWidth={2}
			justifyContent="center"
			alignItems="center"
		>
			<Box height="15px" width="15px" bg="black" borderRadius="100%" zIndex={10} />
			{[...Array(60).keys()].map(key => (
				<Box
					key={key}
					position="absolute"
					bg="black"
					height="10px"
					width="3px"
					style={{
						transform: `rotate(${(key + 1) * 6}deg) translateY(-245px)`,
					}}
				/>
			))}
			<Box // Second hand
				height={size / 2 - 30}
				width="5px"
				bg="reds.300"
				position="absolute"
				style={{
					transform: `rotate(${degSec}deg) translateY(-${(size / 2 - 30) / 2}px)`,
				}}
			/>
			<Box // Minute hand
				height={size / 2 - 30}
				width="5px"
				bg="black"
				position="absolute"
				style={{
					transform: `rotate(${degMin}deg) translateY(-${(size / 2 - 30) / 2}px)`,
				}}
			/>
			<Box // Hour hand
				height={size / 2 - 100}
				width="5px"
				bg="black"
				position="absolute"
				style={{
					transform: `rotate(${degHou}deg) translateY(-${(size / 2 - 100) / 2}px)`,
				}}
			/>
		</Flex>
	);
};
