import React from 'react';

import { useWindowDimensions } from '../hooks';

import { Box } from '../layout';

export const Modal: React.FC = ({ children }) => {
	const dimensions = useWindowDimensions();

	return (
		<Box position="absolute" top={0} right={0} bottom={0} left={0} bg="modalBackground">
			<Box
				width={getModalWidth(dimensions?.width || 0)}
				bg="grays.100"
			>
				{children}
			</Box>
		</Box>
	);
};

const getModalWidth = (width: number) => {
	if (width < 600) return 'modal.width.small';
	if (width < 900) return 'modal.width.medium';
	if (width < 1100) return 'modal.width.large';
};
