import React, { useEffect, useState } from 'react';

import { Flex, Box } from '../../atoms/layout';
import { Button } from '../../atoms/input';

const timeScale = 50;

export const Clock: React.FC = () => {
	const [degSec, setDegSec] = useState(0);
	const [degMin, setDegMin] = useState(0);
	const [degHou, setDegHou] = useState(0);

	useEffect(() => {
		const interval = setInterval(() => setDegSec(degSec + 6), 1000 / timeScale);
		return () => clearInterval(interval);
	}, [degSec]);

	useEffect(() => {
		const interval = setInterval(() => setDegMin(degMin + 6), 1000 * 60 / timeScale);
		return () => clearInterval(interval);
	}, [degMin]);

	useEffect(() => {
		const interval = setInterval(() => setDegHou(degHou + 15), 1000 * 60 * 60 / timeScale);
		return () => clearInterval(interval);
	}, [degHou]);

	return (
		<>
			<Flex
				height="500px"
				width="500px"
				bg="reds.100"
				borderRadius="100%"
				borderStyle="solid"
				borderWidth={2}
				justifyContent="center"
				alignItems="center"
			>
				<Box height="15px" width="15px" bg="black" borderRadius="100%" zIndex={10} />
				<Box
					position="absolute"
					bg="black"
					height="10px"
					width="3px"
					style={{
						transform: 'translateY(-500px)'
					}}
				/>
				<Box // Second hand
					height="220px"
					width="5px"
					bg="reds.300"
					position="absolute"
					style={{
						transform: `rotate(${degSec}deg) translateY(-110px)`,
					}}
				/>
				<Box // Minute hand
					height="220px"
					width="5px"
					bg="black"
					position="absolute"
					style={{
						transform: `rotate(${degMin}deg) translateY(-110px)`,
					}}
				/>
				<Box // Hour hand
					height="190px"
					width="5px"
					bg="black"
					position="absolute"
					style={{
						transform: `rotate(${degHou}deg) translateY(-95px)`,
					}}
				/>
			</Flex>
			<Button onClick={() => {
				setDegSec(0);
				setDegMin(0);
				setDegHou(0);
			}}>Reset clock</Button>
		</>
	);
};
